    public static String inputValidator(String regex, String input, String messageOnError, String example)
    {
        boolean valid = false;
        while (valid == false)
        {

            //allows letters from any language and allows single quotations and minus symbols.
            Pattern pattern = Pattern.compile(regex, Pattern.CASE_INSENSITIVE);
            Matcher matcher = pattern.matcher(input);

            if (matcher.find() == true)
            {
                valid = true;
            }
            else
            {
                Scanner keyboard = new Scanner(System.in);
                System.out.println(messageOnError);
                System.out.println("example: " + example);
                System.out.print("Input: ");
                input = keyboard.next();
                newLine();

            }
        }
        return input;
    }

    public static void newLine()
    {
        System.out.println("");
    }

    public static String inputName()
    {

        String name = "";
        boolean valid = false;
        while (valid == false)
        {
            name = inputString();

            //allows letters from any language and allows single quotations and minus symbols.
            String regx = "[\\p{L} '-]+$";
            Pattern pattern = Pattern.compile(regx, Pattern.CASE_INSENSITIVE);
            Matcher matcher = pattern.matcher(name);

            if (matcher.find() == true)
            {
                valid = true;
            }
            else
            {
                System.out.println("Invalid characters entered, try again.");
                System.out.print("Name: ");
            }
        }
        return name;
    }

    public static LocalDate inputDate()
    {
        LocalDate date = null;
        boolean valid = false;
        while (valid == false)
        {

            Scanner keyboard = new Scanner(System.in);
            String inputDate = keyboard.next();
            try
            {
                date = LocalDate.parse(inputDate);
                valid = true;
            }
            catch (DateTimeParseException e)
            {
                System.out.println("Invalid date, try again.");
                System.out.println("example: " + LocalDate.now());
                System.out.print("Date: ");
            }
        }
        return date;
    }
